message = ""


def findMarker(mes):
    l = len(mes)
    i = 0
    while i < l:
        if not mes[i] in mes[i+1:i+4]:
            if not mes[i+1] in mes[i+2:i+4]:
                if mes[i+2] != mes[i+3]:
                    return i+4
        i = i+1


def checkMarker(m, i, n):
    print("-",m[i] , m[i:n])

    if not m[i] in m[i:n]:
        return True
    return False


def findMarker14(mes):
    l = len(mes)
    i = 0
    while i < l:
        j = 1
        while j <= 14:
            if j == 13:
                if mes[i+12] != mes[i+13]:
                    return i+14

            print(i, j, i+14)
            isMarker = checkMarker(mes, i, i+j)
            if not isMarker:
                print("continue", i)
                i = i+1
                continue
            
            j = j+1
        i = i+1


print("Part 1")
